mapper.select:
select d.*, c.name as category_name from sky_take_out.dish d
left join sky_take_out.category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.update_time desc

SELECT COUNT(*) FROM dish WHERE id IN
#{id}
AND status = 1

mapper.delete:
delete from sky_take_out.dish
where id in

    #{id}

mapper.update:
update sky_take_out.dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select id, name, category_id, price, image, description, status, create_time, update_time, create_user, update_user from sky_take_out.dish

    where category_id = #{categoryId}


    and status = #{status}
